    "category": "config"
  },
  {
    "path": "scripts/ci_lint_compile.py",
    "description": "Python CI helper that performs formatting/lint checks and a compilation pass (AST/bytecode import) and writes detailed logs for artifact upload on failure.",
    "category": "support"
  },
  {
    "path": "scripts/validate_examples_schema.py",
    "description": "Python script that validates repository example files against the project JSON Schema and emits a diff-like report for any violations.",
    "category": "support"
  },
  {
    "path": "scripts/bench_repro_check.py",
    "description": "Python script that re-runs benchmarks in a controlled way, compares results to a committed baseline within tolerances, and writes a human-readable diff plus machine-readable JSON.",
    "category": "support"
  },
  {
    "path": "benchmarks/baseline.json",
    "description": "Committed benchmark baseline results used by CI to verify benchmark reproduction within defined tolerances.",
    "category": "config"
  },
  {
    "path": "pyproject.toml",
    "description": "Project configuration for toolchain (ruff/black/pytest) and dependency extras required by CI linting, schema validation, and benchmarking scripts.",
    "category": "config"
  }
]

