    "category": "documentation"
  },
  {
    "path": "scripts/e2e/validate_config.py",
    "description": "Utility script that validates workflow/action inputs, required env vars, and expected artifact paths to prevent drift across modularized e2e components.",
    "category": "support"
  },
  {
    "path": "scripts/e2e/collect_diagnostics.sh",
    "description": "Portable diagnostics collection script invoked on failure to gather logs, screenshots, test reports, and system metadata into a single artifact directory.",
    "category": "support"
  }
]

