[
  {
    "path": "src/api_server.py",
    "description": "FastAPI server exposing endpoints to submit DOI lists, run open-full-text discovery against multiple providers, and return structured results with logs.",
    "category": "source"
  },
  {
    "path": "src/discovery.py",
    "description": "Implements provider-ordered DOI-to-fulltext discovery (Unpaywall, OpenAlex, Crossref links, PubMed/PMC) with normalized result objects and retry/backoff.",
    "category": "source"
  },
  {
    "path": "src/providers.py",
    "description": "Contains provider clients and fetch logic for Unpaywall, OpenAlex, Crossref, and NCBI E-utilities/PMC with robust error handling and rate limiting.",
    "category": "source"
  },
  {
    "path": "src/models.py",
    "description": "Defines Pydantic models for requests/responses, per-DOI outcomes, provenance metadata, and export-ready run summaries.",
    "category": "source"
  },
  {
    "path": "src/storage.py",
    "description": "Persists run inputs/outputs under outputs/ with deterministic run IDs, NDJSON/JSON exports, and filesystem-safe naming.",
    "category": "source"
  },
  {
    "path": "src/logging_utils.py",
    "description": "Sets up structured logging (JSON lines) for provider attempts, timing, success/failure reasons, and request correlation IDs.",
    "category": "support"
  },
  {
    "path": "src/config.py",
    "description": "Centralizes configuration via environment variables (API keys/emails, timeouts, concurrency, output directory) with sane defaults.",
    "category": "config"
  },
  {
    "path": "src/cli.py",
    "description": "Command-line entrypoint to run the same DOI discovery pipeline without the server and save results to outputs/ for MVP batch use.",
    "category": "source"
  },
  {
    "path": "requirements.txt",
    "description": "Pins runtime dependencies for the API server, HTTP clients, and data models required by the prototype.",
    "category": "config"
  },
  {
    "path": "README.md",
    "description": "Documents how to run the API server and CLI, configure keys/emails, input DOI lists, and interpret outputs and logs in outputs/.",
    "category": "documentation"
  }
]

