    "category": "support"
  },
  {
    "path": "tests/test_cli_end_to_end.py",
    "description": "End-to-end tests exercising core CLI commands (init, validate, flag, variant, cite, export-eval) to ensure functional workflows and correct outputs.",
    "category": "test"
  },
  {
    "path": "tests/test_schema_and_provenance.py",
    "description": "Unit tests covering metadata schema validation, provenance flag rules, variant numbering determinism, and repository citation link generation.",
    "category": "test"
  }
]

