    "path": "src/json_cli_tool/version.py",
    "description": "Defines the canonical tool version string and helpers to include it in structured logs.",
    "category": "source"
  },
  {
    "path": "src/json_cli_tool/logging_utils.py",
    "description": "Implements run directory creation under outputs/logs/run_YYYYMMDD_HHMMSS and utilities to write console and JSON structured logs atomically.",
    "category": "source"
  },
  {
    "path": "src/json_cli_tool/process_runner.py",
    "description": "Provides a safe subprocess execution wrapper that captures stdout/stderr, exit code, timing, and command metadata for logging.",
    "category": "source"
  },
  {
    "path": "src/json_cli_tool/cli.py",
    "description": "Implements the JSON CLI entrypoint that runs artifact gate and taxonomy validator against outputs/artifacts and writes console output plus structured JSON log to outputs/logs/run_YYYYMMDD_HHMMSS.",
    "category": "source"
  },
  {
    "path": "src/json_cli_tool/__main__.py",
    "description": "Allows running the tool via python -m json_cli_tool by delegating to the CLI main function.",
    "category": "source"
  },
  {
    "path": "pyproject.toml",
    "description": "Configures packaging and defines a console script entrypoint for the CLI tool with its dependencies and metadata.",
    "category": "config"
  },
  {
    "path": "README.md",
    "description": "Documents installation and usage of the CLI tool, expected inputs/outputs, log formats, and example commands.",
    "category": "documentation"
  }
]

