[
  {
    "path": "src/id_schema.py",
    "description": "Defines the canonical ID schema (compiled regex patterns, normalization rules, and uniqueness constraints) used across extraction CSV, taxonomy JSONL, and prereg fields.",
    "category": "source"
  },
  {
    "path": "src/loaders.py",
    "description": "Implements robust readers/parsers for the extraction CSV, taxonomy JSONL, and prereg metadata (CSV/JSON) into a unified in-memory representation with validation-friendly field names.",
    "category": "source"
  },
  {
    "path": "src/id_mismatch_checker.py",
    "description": "Implements the ID mismatch checker that validates schema conformance, detects duplicates, and cross-checks referential integrity across extraction/taxonomy/prereg sources with actionable error reports.",
    "category": "source"
  },
  {
    "path": "src/demo_run.py",
    "description": "Provides a runnable CLI demo that loads the included demo dataset and intentionally fails to demonstrate the checker catching ID schema and cross-file mismatch errors.",
    "category": "source"
  },
  {
    "path": "data/demo/extraction.csv",
    "description": "Small demo extraction CSV containing intentionally malformed and mismatching IDs to prove the checker flags schema violations and missing/extra references.",
    "category": "support"
  },
  {
    "path": "data/demo/taxonomy.jsonl",
    "description": "Small demo taxonomy JSONL with deliberate ID conflicts and missing IDs relative to the extraction CSV for mismatch detection.",
    "category": "support"
  },
  {
    "path": "data/demo/prereg.json",
    "description": "Small demo preregistration metadata file containing prereg fields with IDs that intentionally disagree with the extraction and taxonomy sources.",
    "category": "support"
  }
]

