[
  {
    "path": "README.md",
    "description": "Project overview and quickstart explaining how to generate multi-wave randomized intervention trial plans linking cognitive growth, ZPD social support operationalizations, and decision outcomes.",
    "category": "documentation"
  },
  {
    "path": "pyproject.toml",
    "description": "Python package configuration with pinned dependencies and entry points for running the trial-plan generator CLI.",
    "category": "config"
  },
  {
    "path": "src/trialplanner/__init__.py",
    "description": "Package initializer exporting the public API for building, validating, and rendering multi-wave trial plans.",
    "category": "source"
  },
  {
    "path": "src/trialplanner/schema.py",
    "description": "Pydantic data models defining experimental arms, ZPD support parameters (type/timing/fading), measurement schedules, mediators/outcomes, and causal tests for transfer and durability.",
    "category": "source"
  },
  {
    "path": "src/trialplanner/catalogs.py",
    "description": "Curated catalogs of intervention components, cognitive tasks, proximal mediator measures, distal outcomes, and example ZPD operationalizations used to assemble mechanism-oriented plans.",
    "category": "source"
  },
  {
    "path": "src/trialplanner/design.py",
    "description": "Core design engine that constructs multi-wave randomized intervention trials with specified arms (choice-architecture, debiasing, sleep-restoration), schedules, and logistics enabling within-cohort transfer/durability comparisons.",
    "category": "source"
  },
  {
    "path": "src/trialplanner/causal.py",
    "description": "Causal-chain planning utilities that generate mediation/moderation test specifications, estimands, identification assumptions, and analysis-ready model formulas for longitudinal RCTs.",
    "category": "source"
  },
  {
    "path": "src/trialplanner/randomization.py",
    "description": "Randomization module supporting multi-wave assignment (individual/cluster, stratified, re-randomization) and producing reproducible allocation tables for experimental arms and ZPD variants.",
    "category": "source"
  },
  {
    "path": "src/trialplanner/render.py",
    "description": "Renderers that output the complete trial plan as structured JSON and human-readable Markdown including arms, timelines, measures, causal tests, and logistics checklists.",
    "category": "source"
  },
  {
    "path": "src/trialplanner/cli.py",
    "description": "Command-line interface to generate, validate, and export detailed mechanism-oriented multi-wave trial plans from a YAML/JSON spec or defaults.",
    "category": "source"
  },
  {
    "path": "src/trialplanner/sample_spec.yaml",
    "description": "Example configuration specifying cohort structure, waves, intervention arms, ZPD support manipulation factors, measures, and durability/transfer comparison settings.",
    "category": "support"
  },
  {
    "path": "tests/test_end_to_end_plan.py",
    "description": "End-to-end tests that build a full multi-wave plan and assert required components exist (arms, ZPD factors, schedules, mediators/outcomes, causal-chain tests, and logistics).",
    "category": "test"
  }
]

