[
  {
    "path": ".github/workflows/ci.yml",
    "description": "GitHub Actions workflow that sets up Python, installs dependencies, runs the validator and demo runner, hard-fails on missing expected outputs (reports/figures), and uploads runtime/_build as CI artifacts.",
    "category": "config"
  },
  {
    "path": "scripts/ci_run.py",
    "description": "Python CI entrypoint that runs the repo's validator and demo runner commands, verifies required output paths exist, and exits nonzero with actionable diagnostics when outputs are missing.",
    "category": "support"
  },
  {
    "path": "scripts/ci_expectations.json",
    "description": "Declarative list of required CI output files/directories (e.g., reports and figures) used by the CI runner to enforce hard failures when artifacts are missing.",
    "category": "config"
  },
  {
    "path": "scripts/README_CI_EXAMPLES.md",
    "description": "Examples documenting how to run the CI checks locally (validator + demo runner + output assertions) and how to customize expected outputs.",
    "category": "documentation"
  }
]

