[
  {
    "path": "src/ids/convention.py",
    "description": "Defines the canonical StudyID/EffectID format, validation regexes, and helpers to normalize/compare IDs across files.",
    "category": "source"
  },
  {
    "path": "src/ids/checker.py",
    "description": "Implements a CLI-capable checker that loads CSV, JSONL, and prereg templates to report missing, duplicate, and mismatched StudyID/EffectID values with actionable error messages.",
    "category": "source"
  },
  {
    "path": "src/ids/io.py",
    "description": "Provides robust readers/writers for CSV and JSONL and a parser for prereg templates to extract and validate ID fields consistently.",
    "category": "source"
  },
  {
    "path": "src/ids/reporting.py",
    "description": "Builds structured check results and renders human-readable and machine-readable summaries of ID integrity issues.",
    "category": "source"
  },
  {
    "path": "src/demo/run_demo.py",
    "description": "Runs the checker against the included demo dataset to intentionally trigger an ID mismatch and prints the expected failure behavior.",
    "category": "source"
  },
  {
    "path": "data/demo/effects.csv",
    "description": "Demo CSV dataset containing StudyID and EffectID columns including an intentional mismatch/duplicate to exercise the checker.",
    "category": "support"
  },
  {
    "path": "data/demo/effects.jsonl",
    "description": "Demo JSONL dataset containing StudyID and EffectID fields including an intentional mismatch relative to the CSV to exercise cross-file checks.",
    "category": "support"
  },
  {
    "path": "data/demo/prereg_template.md",
    "description": "Demo prereg template embedding StudyID and EffectID fields with an intentional mismatch to document expected checker failure behavior.",
    "category": "support"
  },
  {
    "path": "docs/id_convention.md",
    "description": "Documents the canonical StudyID/EffectID convention with examples, rationale, and how to add the ID fields to CSV, JSONL, and prereg templates.",
    "category": "documentation"
  },
  {
    "path": "docs/demo_expected_failures.md",
    "description": "Explains the demo dataset’s intentional ID problems and enumerates the exact missing/duplicate/mismatched errors the checker should report.",
    "category": "documentation"
  },
  {
    "path": "tests/test_id_checker_demo.py",
    "description": "Automated tests that run the checker on the demo dataset and assert the expected mismatch/duplicate/missing ID errors are detected.",
    "category": "test"
  }
]

