    "category": "config"
  },
  {
    "path": "README.md",
    "description": "User-facing documentation describing the mission, installation, CLI usage, workflow outputs, and validation approach (survey + audit study).",
    "category": "documentation"
  },
  {
    "path": "src/psyprim/__init__.py",
    "description": "Package initializer exposing the public version and high-level APIs for roadmap generation and detection.",
    "category": "source"
  },
  {
    "path": "src/psyprim/cli.py",
    "description": "Complete argparse-based CLI implementing commands to generate standardized workflows, metadata checklists, survey/audit instruments, and to run lightweight primary-source citation detection on text files.",
    "category": "source"
  },
  {
    "path": "src/psyprim/schemas.py",
    "description": "Dataclasses and JSON-serializable schemas for workflows, metadata checklists, survey instruments, audit study design, and detection results.",
    "category": "source"
  },
  {
    "path": "src/psyprim/roadmap.py",
    "description": "Actionable roadmap generator that outputs concrete study designs (surveys + audit studies), measurement instruments, required repositories/data sources, and specialist-agent task assignments.",
    "category": "source"
  },
  {
    "path": "src/psyprim/instruments.py",
    "description": "Templates and builders for survey items, audit-study coding sheets, metadata checklists, and rubrics aligned to primary-source scholarship validation goals.",
    "category": "source"
  },
  {
    "path": "src/psyprim/detectors.py",
    "description": "Lightweight detection features for primary-source scholarship including edition/translation provenance signals, variant pagination heuristics, and repository citation/identifier extraction.",
    "category": "source"
  },
  {
    "path": "src/psyprim/repositories.py",
    "description": "Curated registry of target repositories and identifier patterns (e.g., DOI, JSTOR, HathiTrust, Internet Archive, PsycINFO, PubMed) used by detectors and roadmap outputs.",
    "category": "source"
  },
  {
    "path": "tests/test_detectors.py",
    "description": "Pytest suite validating citation/identifier extraction and key detection heuristics with representative text fixtures and edge cases.",
    "category": "test"
  },
  {
    "path": "tests/test_roadmap_outputs.py",
    "description": "Pytest suite ensuring generated roadmap, checklist, and instrument outputs are structurally valid, deterministic, and include required mission elements.",
    "category": "test"
  },
  {
    "path": "src/psyprim/_data/defaults.json",
    "description": "Embedded default configuration containing repository registry entries, checklist fields, and baseline survey/audit instrument item banks.",
    "category": "support"
  }
]

