[
  {
    "path": "README.md",
    "description": "Project overview and quickstart describing mission goals, supported workflows, installation, and how to run the planner and validation protocols.",
    "category": "documentation"
  },
  {
    "path": "pyproject.toml",
    "description": "Python package configuration defining dependencies, entry points, tooling, and test settings for the workflow/provenance planning toolkit.",
    "category": "config"
  },
  {
    "path": "src/psyprov/__init__.py",
    "description": "Package initializer exporting public interfaces for schemas, heuristics, and the plan generator.",
    "category": "source"
  },
  {
    "path": "src/psyprov/schemas.py",
    "description": "Pydantic data models and JSON Schema exporters for metadata fields covering edition/translation provenance, page/paragraph variants, and repository citations.",
    "category": "source"
  },
  {
    "path": "src/psyprov/heuristics.py",
    "description": "Deterministic, lightweight detection heuristics for inferring provenance signals from citations, filenames, OCR text, and repository metadata.",
    "category": "source"
  },
  {
    "path": "src/psyprov/checklists.py",
    "description": "Structured, testable checklist specifications for authors, archivists, and journals including required metadata, validation rules, and escalation paths.",
    "category": "source"
  },
  {
    "path": "src/psyprov/uiux_requirements.py",
    "description": "Machine-readable UI/UX requirement specs for plugins and submission systems including forms, defaults, error states, and reviewer-facing displays.",
    "category": "source"
  },
  {
    "path": "src/psyprov/evaluation_protocol.py",
    "description": "Empirical evaluation protocol generator detailing survey instruments, audit-study designs, sampling plans, outcome metrics, and analysis templates.",
    "category": "source"
  },
  {
    "path": "src/psyprov/plan_generator.py",
    "description": "Prioritized task breakdown generator that composes checklists, schema, UI/UX requirements, heuristics, and evaluation protocols into a concrete, handoff-ready plan.",
    "category": "source"
  },
  {
    "path": "src/psyprov/cli.py",
    "description": "Command-line interface to output the complete prioritized mission plan as JSON/Markdown and to export schemas and protocol documents for partners.",
    "category": "source"
  },
  {
    "path": "tests/test_plan_outputs.py",
    "description": "End-to-end tests validating schema correctness, heuristic determinism, and that generated plans include required sections, priorities, and protocol components.",
    "category": "test"
  },
  {
    "path": "support/sample_inputs/",
    "description": "Directory containing small sample citation strings, OCR snippets, and repository metadata fixtures used by tests and demo runs.",
    "category": "support"
  }
]

