[
  {
    "path": ".github/workflows/ci.yml",
    "description": "GitHub Actions CI workflow that sets up Python, installs dependencies, runs the one-command runner (gate→validator→meta-analysis demo), verifies required outputs exist, and uploads runtime/_build as an artifact.",
    "category": "config"
  },
  {
    "path": "scripts/ci_verify_outputs.py",
    "description": "Python helper script invoked by CI to hard-fail the job when required output files/directories (including runtime/_build) are missing after the runner completes.",
    "category": "support"
  },
  {
    "path": "examples/ci_github_actions_minimal.md",
    "description": "Example documentation showing how the minimal GitHub Actions workflow runs the one-command runner and how to reproduce the same steps locally, including output verification expectations.",
    "category": "documentation"
  }
]

