[
  {
    "path": "src/meta_starter/__init__.py",
    "description": "Package initializer for the minimal meta-analysis starter kit used by the runnable analysis skeleton.",
    "category": "source"
  },
  {
    "path": "src/meta_starter/io.py",
    "description": "Implements CSV template/schema handling and robust loading/validation of extracted study-level effect size data.",
    "category": "source"
  },
  {
    "path": "src/meta_starter/meta.py",
    "description": "Provides core meta-analysis computations (toy fixed-effect and random-effects pooling) and produces numeric summary tables.",
    "category": "source"
  },
  {
    "path": "src/meta_starter/report.py",
    "description": "Builds and writes analysis outputs (summary CSV tables) into runtime/outputs/_build/ in a reproducible structure.",
    "category": "source"
  },
  {
    "path": "runtime/outputs/README.md",
    "description": "Explains how to use the starter kit templates and run the analysis to generate at least one numeric summary table in runtime/outputs/_build/.",
    "category": "documentation"
  },
  {
    "path": "runtime/outputs/templates/effect_sizes_template.csv",
    "description": "CSV extraction template defining the standardized study-level inputs required to run the starter meta-analysis workflow.",
    "category": "support"
  },
  {
    "path": "runtime/outputs/templates/toy_effect_sizes.csv",
    "description": "Small toy dataset (filled example) matching the extraction template to enable a fully runnable end-to-end meta-analysis output.",
    "category": "support"
  },
  {
    "path": "runtime/outputs/run_meta_analysis.py",
    "description": "Runnable analysis skeleton script that loads the toy or user-filled CSV, runs pooling, and saves numeric summary outputs to runtime/outputs/_build/.",
    "category": "source"
  },
  {
    "path": "runtime/outputs/_build/.gitkeep",
    "description": "Keeps the build output directory tracked so generated summary tables can be written to runtime/outputs/_build/ during runs.",
    "category": "support"
  }
]

