[
  {
    "path": "docker/Dockerfile",
    "description": "Dockerfile that builds a pinned, reproducible runner image and executes the smoke-test before running the JSON-driven pipeline.",
    "category": "config"
  },
  {
    "path": "docker/.dockerignore",
    "description": "Docker ignore rules to keep builds deterministic and small by excluding caches, artifacts, and local virtualenvs.",
    "category": "config"
  },
  {
    "path": "requirements.lock.txt",
    "description": "Pinned Python dependency lock manifest used to prevent environment drift and stabilize executions.",
    "category": "config"
  },
  {
    "path": "environment.manifest.json",
    "description": "Minimal reproducibility manifest recording intended OS/Python/tooling expectations and how to validate them.",
    "category": "config"
  },
  {
    "path": "scripts/smoke_test.py",
    "description": "Tiny smoke-test that validates Python/runtime, imports critical deps, and writes an environment fingerprint JSON for run logs.",
    "category": "test"
  },
  {
    "path": "scripts/record_versions.py",
    "description": "Utility that captures package, Python, and OS versions and injects them into JSON run logs alongside the runner.",
    "category": "support"
  },
  {
    "path": "runner/run_pipeline.py",
    "description": "JSON-script runner entrypoint that runs the smoke-test first, then executes validators/meta-analysis, and persists raw logs plus version metadata.",
    "category": "source"
  },
  {
    "path": "runner/schema/run_log.schema.json",
    "description": "JSON Schema defining the run-log format including environment/version fields to standardize persisted execution records.",
    "category": "config"
  },
  {
    "path": "runner/schema/run_spec.schema.json",
    "description": "JSON Schema defining the JSON script/run specification including steps for validation and meta-analysis with reproducibility settings.",
    "category": "config"
  },
  {
    "path": "scripts/validate_json.py",
    "description": "Schema validation tool that verifies run specs and run logs against their schemas prior to execution and persistence.",
    "category": "support"
  },
  {
    "path": "scripts/entrypoint.sh",
    "description": "Container entrypoint that enforces deterministic startup, runs smoke-test, and then launches the runner with strict error handling.",
    "category": "support"
  },
  {
    "path": "README.reproducibility.md",
    "description": "Documentation describing how to build/run via Docker, how manifests are used, and where environment versions are recorded in JSON logs.",
    "category": "documentation"
  }
]

