[
  {
    "path": "README.md",
    "description": "Documents the atomic-claim schema, labeling rules, reference corpus format, retrieval/audit pipeline usage, and tiered false-accept/abstain metrics.",
    "category": "documentation"
  },
  {
    "path": "pyproject.toml",
    "description": "Defines project metadata, runtime dependencies (e.g., pydantic, numpy, scikit-learn), and console entry points for running audits.",
    "category": "config"
  },
  {
    "path": "src/claims_audit/__init__.py",
    "description": "Exposes the public package API for claim schema, retrieval, auditing, and metrics computation.",
    "category": "source"
  },
  {
    "path": "src/claims_audit/schema.py",
    "description": "Implements the atomic-claim schema with machine-readable fields (IDs, subject/predicate/object, qualifiers, timeframe, modality, normalization) and label definitions (supported/unsupported/insufficient).",
    "category": "source"
  },
  {
    "path": "src/claims_audit/corpus.py",
    "description": "Loads and validates a small curated reference corpus (documents and evidence spans) from JSONL/YAML and provides search-ready text views plus metadata.",
    "category": "source"
  },
  {
    "path": "src/claims_audit/retrieval.py",
    "description": "Provides a lightweight retrieval layer (BM25/TF-IDF) over the reference corpus with top-k evidence selection and deterministic scoring.",
    "category": "source"
  },
  {
    "path": "src/claims_audit/audit.py",
    "description": "Runs claim-level audits by retrieving evidence for each atomic claim, applying decision logic to assign supported/unsupported/insufficient, and emitting per-claim rationales and traces.",
    "category": "source"
  },
  {
    "path": "src/claims_audit/metrics.py",
    "description": "Computes tiered metrics including false-accept rate, abstain rate, coverage, and confusion matrices by confidence tier and label.",
    "category": "source"
  },
  {
    "path": "src/claims_audit/io.py",
    "description": "Handles reading/writing atomic claims, audit outputs, and metric reports in JSONL with stable schemas and versioning fields.",
    "category": "source"
  },
  {
    "path": "src/claims_audit/cli.py",
    "description": "Implements a CLI to validate inputs, build/load the corpus index, run audits, and print/save tiered metric summaries.",
    "category": "source"
  },
  {
    "path": "data/reference_corpus.jsonl",
    "description": "Provides a small curated reference corpus with a handful of documents and evidence spans used for retrieval-backed claim audits.",
    "category": "support"
  },
  {
    "path": "data/example_claims.jsonl",
    "description": "Contains example atomic claims with gold labels to demonstrate auditing and enable end-to-end evaluation of false-accept/abstain metrics.",
    "category": "support"
  }
]

