[
  {
    "path": "README.md",
    "description": "Project overview and quickstart describing how the Python CLI generates community protocol drafts, technical requirements, validation study designs, and phased rollout milestones for provenance-aware primary-source psychology scholarship.",
    "category": "documentation"
  },
  {
    "path": "pyproject.toml",
    "description": "Python project configuration defining dependencies, entry points for the CLI, and tooling settings for reproducible execution.",
    "category": "config"
  },
  {
    "path": "src/protocol_planner/__init__.py",
    "description": "Package initializer exposing the public CLI entry function and version metadata.",
    "category": "source"
  },
  {
    "path": "src/protocol_planner/cli.py",
    "description": "Command-line interface that collects inputs (scope, corpora, stakeholders, repositories) and writes a complete implementable plan plus artifacts into an output directory.",
    "category": "source"
  },
  {
    "path": "src/protocol_planner/models.py",
    "description": "Typed dataclass models for stakeholders, protocol components (checklists, schemas), plugin/software requirements, study designs (survey + audit), and rollout milestones with measurable metrics.",
    "category": "source"
  },
  {
    "path": "src/protocol_planner/generate_plan.py",
    "description": "End-to-end plan generator that composes stakeholder engagement steps, technical requirements, validation designs, and phased rollout into a structured markdown plan with traceable acceptance criteria.",
    "category": "source"
  },
  {
    "path": "src/protocol_planner/artifacts.py",
    "description": "Artifact writers that emit draft protocol checklists, metadata schema (JSON Schema), repository citation guidelines, and plugin/spec stubs as separate files alongside the main plan.",
    "category": "source"
  },
  {
    "path": "src/protocol_planner/provenance_detection_spec.py",
    "description": "Detailed technical specification generator for lightweight detection/annotation software/plugins covering edition/translation provenance, variant pagination/paragraph markers, and public-domain repository citations.",
    "category": "source"
  },
  {
    "path": "src/protocol_planner/validation_studies.py",
    "description": "Generators for implementable survey instruments and audit study protocols including sampling plans, coding rubrics, inter-rater reliability measures, and analysis plans for adoption/accuracy evaluation.",
    "category": "source"
  },
  {
    "path": "src/protocol_planner/rollout.py",
    "description": "Phased rollout planner that produces timelines, governance checkpoints, adoption targets, and evaluation milestones (usage metrics, endorsement counts, quality/accuracy thresholds).",
    "category": "source"
  },
  {
    "path": "src/protocol_planner/data/default_templates.py",
    "description": "Built-in default templates and controlled vocabularies for stakeholder roles, checklist items, metadata fields, repository citation forms, and milestone metric definitions.",
    "category": "support"
  },
  {
    "path": "tests/test_generate_plan.py",
    "description": "Automated tests verifying the CLI and generators produce deterministic, complete outputs (plan + artifacts) containing required sections, measurable milestones, and valid JSON Schema.",
    "category": "test"
  }
]

