[
  {
    "path": "tools/consolidate_build_scripts.py",
    "description": "CLI tool that scans agent output directories for duplicate build_runner.py and verify_artifacts.py, selects the best candidates via heuristics, normalizes paths/imports so the runner always writes to runtime/_build/, and installs the canonical pair into a stable repo location.",
    "category": "source"
  },
  {
    "path": "scripts/build_runner.py",
    "description": "Canonical build runner that executes the standardized build workflow and always writes artifacts and logs under runtime/_build/ regardless of invocation directory.",
    "category": "source"
  },
  {
    "path": "scripts/verify_artifacts.py",
    "description": "Canonical artifact verification script that runs the required validation tools against the current artifacts under runtime/_build/ and persists raw command outputs and a summarized report.",
    "category": "source"
  },
  {
    "path": "scripts/_build_common.py",
    "description": "Shared utilities used by the canonical runner and verifier for path resolution, subprocess execution, logging, and report persistence to runtime/_build/.",
    "category": "source"
  },
  {
    "path": "docs/build_script_consolidation.md",
    "description": "Documentation describing how consolidation works, how candidate scripts are scored/selected, where the canonical scripts live, and the invariant that all outputs go to runtime/_build/.",
    "category": "documentation"
  }
]

