    "category": "config"
  },
  {
    "path": "README.md",
    "description": "User-facing documentation describing the mission, CLI commands, example workflows, and how to generate the validation/adoption roadmap outputs.",
    "category": "documentation"
  },
  {
    "path": "src/psyprim/__init__.py",
    "description": "Package initializer exposing version and top-level interfaces for the psyprim toolkit.",
    "category": "source"
  },
  {
    "path": "src/psyprim/cli.py",
    "description": "Complete CLI implementation that generates a rigorous validation and adoption roadmap with selectable study designs, metrics, sampling frames, and analysis plans.",
    "category": "source"
  },
  {
    "path": "src/psyprim/schemas.py",
    "description": "Pydantic models and JSON Schema export utilities for roadmap specifications, including experiments, metrics, sampling frames, and data collection/analysis methods.",
    "category": "source"
  },
  {
    "path": "src/psyprim/roadmap.py",
    "description": "Core logic to assemble empirically grounded roadmap plans, including default templates for surveys, audit studies, and adoption experiments tailored to psychology scholarship.",
    "category": "source"
  },
  {
    "path": "src/psyprim/metrics.py",
    "description": "Definitions and computation scaffolds for citation accuracy, reproducibility, and usability metrics with operationalizations suitable for evaluation studies.",
    "category": "source"
  },
  {
    "path": "src/psyprim/sampling.py",
    "description": "Sampling-frame builders for journals, archives, and researcher populations with stratification options and reproducible selection procedures.",
    "category": "source"
  },
  {
    "path": "src/psyprim/analysis.py",
    "description": "Analysis-plan generator specifying statistical models, power/MDES heuristics, qualitative coding approaches, and reporting templates for adoption-effect estimation.",
    "category": "source"
  },
  {
    "path": "src/psyprim/io.py",
    "description": "Input/output utilities to read optional user overrides and export roadmap artifacts as JSON, YAML, and Markdown reports with deterministic formatting.",
    "category": "source"
  }
]

