    "category": "config"
  },
  {
    "path": "README.md",
    "description": "Usage-focused documentation describing the retrieve-then-verify pipeline, data formats, calibration, and audit logs.",
    "category": "documentation"
  },
  {
    "path": "src/verifier_policy/__init__.py",
    "description": "Package initializer exposing the primary pipeline entry points and version information.",
    "category": "source"
  },
  {
    "path": "src/verifier_policy/schemas.py",
    "description": "Typed dataclasses/Pydantic-style models defining inputs/outputs for claims, passages, quotes, decisions, and audit events.",
    "category": "source"
  },
  {
    "path": "src/verifier_policy/retrieval.py",
    "description": "Retriever implementation that fetches top-k source passages with deterministic caching and normalized provenance metadata.",
    "category": "source"
  },
  {
    "path": "src/verifier_policy/quote_alignment.py",
    "description": "Quote-level alignment module that maps each decomposed claim to minimal supporting/refuting spans within retrieved passages.",
    "category": "source"
  },
  {
    "path": "src/verifier_policy/constraints.py",
    "description": "Deterministic constraint checks enforcing required evidence, quote coverage, provenance validity, and other policy rules.",
    "category": "source"
  },
  {
    "path": "src/verifier_policy/verifier.py",
    "description": "Core verifier that performs claim decomposition orchestration, retrieve-then-verify, applies constraints, and emits per-claim decisions with evidence.",
    "category": "source"
  },
  {
    "path": "src/verifier_policy/calibration.py",
    "description": "Threshold calibration utilities that fit and apply calibrated decision thresholds on a held-out set and report metrics.",
    "category": "source"
  },
  {
    "path": "src/verifier_policy/audit_log.py",
    "description": "Structured audit logging that records per-claim evidence failures, constraint violations, and provenance for downstream review.",
    "category": "support"
  },
  {
    "path": "src/verifier_policy/record_versions.py",
    "description": "Utility to capture and persist Python/OS/library version fingerprints to ensure deterministic, auditable runs.",
    "category": "support"
  },
  {
    "path": "src/main.py",
    "description": "Runnable CLI that loads a dataset, runs retrieve-then-verify, calibrates thresholds on held-out data, and writes decisions plus audit logs to disk.",
    "category": "source"
  }
]

