[
  {
    "path": "src/build_runner.py",
    "description": "Implements a one-command Python build runner that sequentially runs artifact gate, taxonomy validation, and the toy meta-analysis demo while forcing all logs/outputs into _build/ and exiting non-zero on any missing/empty artifact.",
    "category": "source"
  },
  {
    "path": "src/build_utils.py",
    "description": "Provides shared utilities for running subprocess steps with tee-style logging to _build/, ensuring directories exist, and validating required artifacts are present and non-empty.",
    "category": "source"
  },
  {
    "path": "scripts/build.py",
    "description": "CLI entrypoint script that invokes the build runner with sensible defaults and supports local usage and CI execution.",
    "category": "source"
  },
  {
    "path": "scripts/_examples/run_build_examples.py",
    "description": "Contains runnable examples demonstrating successful and failing builds, showing where outputs/logs appear under _build/ and how non-zero exits occur on missing/empty artifacts.",
    "category": "documentation"
  },
  {
    "path": "tests/test_build_runner.py",
    "description": "End-to-end tests that create temporary dummy artifacts and stub commands to verify sequential execution, _build/ output enforcement, and non-zero exit behavior for missing/empty artifacts.",
    "category": "test"
  },
  {
    "path": "pyproject.toml",
    "description": "Configures packaging and tooling, including a console script entrypoint for the build runner and pytest configuration for the included tests.",
    "category": "config"
  }
]

