[
  {
    "path": ".github/workflows/ci.yml",
    "description": "GitHub Actions CI workflow that runs the canonical runner on a tiny fixture dataset, validates runtime/_build/{reports,tables,figures,logs} for presence/non-emptiness/schema, and uploads runtime/_build as an artifact on every run.",
    "category": "config"
  },
  {
    "path": "scripts/ci_validate_build.py",
    "description": "Standalone Python validator invoked by CI that asserts required runtime/_build subdirectories exist, are non-empty, and that key outputs conform to lightweight file/schema checks (e.g., tables have parseable headers, logs are decodable, figures are valid images).",
    "category": "support"
  },
  {
    "path": "fixtures/tiny/README.md",
    "description": "Documentation describing the tiny fixture dataset and how CI uses it to exercise the canonical runner deterministically.",
    "category": "documentation"
  },
  {
    "path": "fixtures/tiny/input.csv",
    "description": "Small deterministic CSV fixture input used by the CI run to generate minimal but complete pipeline outputs.",
    "category": "support"
  },
  {
    "path": "fixtures/tiny/config.yml",
    "description": "Minimal runner configuration pointing the canonical runner at the tiny fixture dataset and directing outputs to runtime/_build for CI validation and artifact upload.",
    "category": "config"
  }
]

