[
  {
    "path": "src/cli_tool/__init__.py",
    "description": "Defines the cli_tool package version and exports the canonical public entrypoints used by the runner and CLI.",
    "category": "source"
  },
  {
    "path": "src/cli_tool/cli.py",
    "description": "Implements the single canonical Typer-based CLI entrypoint with flags for risk thresholds and claim decomposition toggling and dispatches runs to the unified runner.",
    "category": "source"
  },
  {
    "path": "src/cli_tool/runner.py",
    "description": "Implements the single canonical runner that creates one deterministic build directory under runtime/_build/, executes the pipeline, and coordinates structured artifact emission.",
    "category": "source"
  },
  {
    "path": "src/cli_tool/artifacts.py",
    "description": "Provides structured artifact writers for logs, run manifest, and decision traces with stable schemas and deterministic file naming in runtime/_build/.",
    "category": "source"
  },
  {
    "path": "src/cli_tool/determinism.py",
    "description": "Enforces deterministic constraint checks and run reproducibility by normalizing environment inputs, seeding, hashing configs, and validating invariants preflight.",
    "category": "source"
  },
  {
    "path": "src/cli_tool/schemas.py",
    "description": "Defines Pydantic models (or lightweight dataclasses) for run manifest, decision trace events, and risk threshold configuration to ensure structured outputs.",
    "category": "source"
  },
  {
    "path": "src/cli_tool/risk.py",
    "description": "Implements risk-threshold evaluation and reporting utilities used by goal_10/goal_12 sweeps and recorded into the decision trace and manifest.",
    "category": "source"
  },
  {
    "path": "src/cli_tool/decomposition.py",
    "description": "Implements optional claim decomposition logic with a CLI toggle and emits decomposition steps into the decision trace for sweep comparability.",
    "category": "source"
  },
  {
    "path": "tests/test_cli_end_to_end.py",
    "description": "End-to-end tests that invoke the CLI to ensure a single build directory is created and that logs, manifest, and decision traces are written deterministically to runtime/_build/ with the new flags.",
    "category": "test"
  },
  {
    "path": "tests/test_determinism_checks.py",
    "description": "Unit tests for deterministic constraint checks including stable run_id generation, config hashing, and invariant validation failure modes.",
    "category": "test"
  },
  {
    "path": "README.md",
    "description": "Documents the canonical runner usage, artifact layout under runtime/_build/, deterministic guarantees, and CLI flags for risk thresholds and claim decomposition to support goal_10/goal_12 sweeps.",
    "category": "documentation"
  },
  {
    "path": "pyproject.toml",
    "description": "Defines the build metadata and console script entrypoint wiring the single canonical runner CLI, plus dependencies for Typer and structured output modeling.",
    "category": "config"
  }
]

