[
  {
    "path": "scripts/verify_artifacts.py",
    "description": "Implements artifact verification and manifest generation (runtime/_build/manifest.json) including paths, sizes, hashes, timestamps, schema versions, and hard-fails the build if required artifacts are missing or empty.",
    "category": "source"
  },
  {
    "path": "scripts/one_command_runner.py",
    "description": "Updates the one-command runner to invoke artifact verification after the build so failures propagate and the manifest is always emitted.",
    "category": "source"
  },
  {
    "path": ".github/workflows/ci.yml",
    "description": "Updates CI to run artifact verification and upload runtime/_build/manifest.json as a build artifact while failing when required artifacts are absent or empty.",
    "category": "config"
  },
  {
    "path": "config/required_artifacts.json",
    "description": "Defines the required build artifacts (glob/path patterns and minimum size rules) that verify_artifacts.py enforces across local runs and CI.",
    "category": "config"
  }
]

