[
  {
    "path": "README.md",
    "description": "Documents the atomic-claim schema, labeling rubric, dataset format, and how to run the curation/validation scripts end-to-end.",
    "category": "documentation"
  },
  {
    "path": "pyproject.toml",
    "description": "Defines the Python package, dependencies, console scripts, formatting/linting tools, and test configuration.",
    "category": "config"
  },
  {
    "path": "src/atomic_claims/__init__.py",
    "description": "Provides the package entry point and exports the public schema and dataset utilities.",
    "category": "source"
  },
  {
    "path": "src/atomic_claims/schema.py",
    "description": "Implements the atomic-claim data model with machine-readable fields (subject, predicate, scope, provenance requirements) plus JSON serialization and validation.",
    "category": "source"
  },
  {
    "path": "src/atomic_claims/rubric.py",
    "description": "Defines the supported/contradicted/insufficient labeling rubric with decision rules, edge-case guidance, and programmatic checks for label consistency.",
    "category": "source"
  },
  {
    "path": "src/atomic_claims/corpus.py",
    "description": "Implements a small curated reference corpus loader (documents + citations) and utilities to index passages and resolve provenance pointers.",
    "category": "source"
  },
  {
    "path": "src/atomic_claims/gold.py",
    "description": "Defines the gold-annotation format tying atomic claims to evidence spans and labels, including validators for borderline-confidence slice constraints.",
    "category": "source"
  },
  {
    "path": "src/atomic_claims/cli.py",
    "description": "Provides a CLI to initialize a starter corpus, create/edit claim+annotation templates, validate JSONL files, and export a compact dataset slice.",
    "category": "source"
  },
  {
    "path": "data/reference_corpus.jsonl",
    "description": "Contains the curated reference documents/passages with stable IDs and citation metadata used for grounding claim labels.",
    "category": "support"
  },
  {
    "path": "data/gold_annotations.jsonl",
    "description": "Contains the initial borderline-confidence dataset slice with atomic claims, labels, and explicit provenance pointers into the reference corpus.",
    "category": "support"
  },
  {
    "path": "tests/test_validation.py",
    "description": "Tests schema/rubric/gold validators on known-good and known-bad examples to ensure labeling and provenance rules are enforced.",
    "category": "test"
  }
]

