    "category": "source"
  },
  {
    "path": "data/competitive_landscape/competitors_seed.csv",
    "description": "Seed dataset of kit suppliers, integrators, and OEM programs with columns for pricing, warranty, compliance posture, and notes used to generate the initial competitive matrix.",
    "category": "support"
  },
  {
    "path": "data/competitive_landscape/overrides.example.yaml",
