[
  {
    "path": "notebooks/ice_vs_conversion_vs_bev.ipynb",
    "description": "Runnable Jupyter notebook that loads the CSV template, applies default segment assumptions and sensitivity toggles, runs drive-cycle proxies, and compares ICE vs conversion vs BEV TCO/energy outcomes with plots and tables.",
    "category": "source"
  },
  {
    "path": "outputs/template_user_inputs.csv",
    "description": "CSV template for user-editable inputs (vehicle specs, segment selection, scenario toggles, cycle choice, and key financial/operational parameters) that the notebook can ingest directly.",
    "category": "config"
  },
  {
    "path": "src/evconv_config/assumptions.py",
    "description": "Defines default assumptions by segment (fleet, classic, specialty) and validated schema for merging user overrides with defaults.",
    "category": "source"
  },
  {
    "path": "src/evconv_config/drive_cycles.py",
    "description": "Provides preloaded standard drive-cycle proxies (urban/last-mile, mixed, highway) as time-speed profiles plus helper functions to select and resample cycles.",
    "category": "source"
  },
  {
    "path": "src/evconv_config/model.py",
    "description": "Implements simplified comparative models for ICE vs conversion vs BEV including energy use, battery replacement, financing, and utilization impacts using the configured assumptions and cycles.",
    "category": "source"
  },
  {
    "path": "src/evconv_config/io.py",
    "description": "Loads, validates, and writes CSV/structured inputs and outputs, including robust parsing of toggles and scenario grids for sensitivity runs.",
    "category": "source"
  },
  {
    "path": "src/evconv_config/plotting.py",
    "description": "Generates consistent matplotlib plots and summary tables for notebook use (TCO breakdown, energy cost sensitivity, utilization curves, and cycle comparisons).",
    "category": "source"
  },
  {
    "path": "src/evconv_config/__init__.py",
    "description": "Package initializer exposing the public API for assumptions, cycles, IO, and modeling used by the notebook.",
    "category": "source"
  },
  {
    "path": "tests/test_config_workflow.py",
    "description": "Pytest suite that verifies defaults merging, CSV parsing, drive-cycle proxy integrity, and deterministic scenario comparison outputs for ICE vs conversion vs BEV.",
    "category": "test"
  },
  {
    "path": "README.md",
    "description": "Project documentation explaining how to run the notebook, edit the CSV template, choose segments/cycles, and interpret sensitivity toggles and outputs.",
    "category": "documentation"
  }
]

