content:
[
  {
    "path": "src/evconv/config/__init__.py",
    "description": "Package entrypoint that exposes the public configuration API and provides versioned, stable imports for IO, plotting, and workflow config utilities.",
    "category": "source"
  },
  {
    "path": "src/evconv/config/types.py",
    "description": "Defines typed, validated dataclasses (and shared enums/constants) for configuration objects used across IO, plotting, and workflow modules.",
    "category": "source"
  },
  {
    "path": "src/evconv/config/io.py",
    "description": "Implements complete configuration loading/saving/merging utilities (YAML/JSON), schema-like validation hooks, and path resolution for the refactored system.",
    "category": "source"
  },
  {
    "path": "src/evconv/config/plotting.py",
    "description": "Provides a reusable, convention-driven plotting configuration layer (themes, rcParams, figure sizing, colormaps) and helpers to apply/serialize plotting settings.",
    "category": "source"
  },
  {
    "path": "src/evconv/config/workflow.py",
    "description": "Defines the modular workflow configuration model (architectures, stages, parameters, presets) and utilities to assemble and validate end-to-end runs.",
    "category": "source"
  },
  {
    "path": "src/evconv/config/defaults.py",
    "description": "Contains canonical default configurations and named presets for IO, plotting, and workflow that eliminate duplicated inline settings across the codebase.",
    "category": "source"
  },
  {
    "path": "src/evconv/config/errors.py",
    "description": "Centralizes custom exception types and rich error formatting for configuration parsing, validation, and missing/invalid fields.",
    "category": "source"
  },
  {
    "path": "documentation/design_spec.md",
    "description": "Documents the standardized, modular configuration architecture (IO, plotting, workflow), conventions, and examples for maintainable reuse across artifacts.",
    "category": "documentation"
  }
]

